import React,{useState} from 'react'

// const filtre = (tableau, element) => {
//     let value = tableau.filter((valeur) => {
//         return valeur === element
//     })
//     return value
// }

export default function Recherche() {
    const [titre,setTitre] = useState('');
    const [select,setSelect]= useState([])
    const [choose,setChoose] = useState(false)
    let movies = [
        {
            'title': 'Interstellar',
            'type': ['Adventure', 'Drama', 'Sci-Fi'],
            'year': 2014
        },
        {
            'title': 'Independence Day',
            'type': ['Action', 'Adventure', 'Sci-Fi'],
            'year': 1996
        },
        {
            'title': 'Deadpool',
            'type': ['Action', 'Adventure', 'Comedy'],
            'year': 2016
        },
        {
            'title': 'Batman Begins',
            'type': ['Action, Adventure'],
            'year': 2005
        },
        {
            'title': 'Bad Boys',
            'type': ['Action', 'Comedy', 'Crime'],
            'year': 1995
        }
    ];

    const music = new Audio('adf.wav');
    music.play();
    // music.loop =true;
    music.playbackRate = 2;
    // music.pause();

    const filtreVideo = (element)=>{
        if(element.title===titre){
            return element
        }
        // else{
        //     return [{title:''}]
        // }
    }
    return (
        <div>
            <input placeholder='Entrez le titre'
            onChange={(e)=>{setTitre(e.target.value)}}
            ></input>
            <button
            onClick={()=>{setSelect(movies.filter(filtreVideo));setChoose(true);}}
            >Chercher</button>
            {
                choose && <span style={{color:'red'}} onClick={()=>console.log('On veut jouer cette chanson')}>{select[0].title}</span>
            }
        </div>
    )
}
