import React, { useState } from 'react'

const filtre = (tableau, element) => {
    let value = tableau.filter((valeur) => {
        return valeur === element
    })
    return value
}

export default function Recherche() {
    // const [titre,setTitre] = useState('');
    let movies = [
        {
            'title': 'Interstellar',
            'type': ['Adventure', 'Drama', 'Sci-Fi'],
            'year': 2014
        },
        {
            'title': 'Independence Day',
            'type': ['Action', 'Adventure', 'Sci-Fi'],
            'year': 1996
        },
        {
            'title': 'Deadpool',
            'type': ['Action', 'Adventure', 'Comedy'],
            'year': 2016
        },
        {
            'title': 'Batman Begins',
            'type': ['Action, Adventure'],
            'year': 2005
        },
        {
            'title': 'Bad Boys',
            'type': ['Action', 'Comedy', 'Crime'],
            'year': 1995
        }
    ];
    function OldMovies(element) {
        for (let i = 0; i < element.type.length; i++) {
            // film de type "Action" OU "Sci-Fi"
            if (element.type[i] === 'Action' || element.type[i] === 'Sci-Fi') {
                return element;
            }
        }
    }

    console.table(movies.filter(OldMovies));
    return (
        <div>
            <input placeholder='Entrez le titre'
            // onChange={(e)=>{setTitre(e.target.value)}}
            ></input>
            <button
            // onClick={()=>{console.log(filtre(['Aline','Gloire','Rachel','Frank','Alice'],titre))}}
            >Chercher</button>
        </div>
    )
}
