import React,{useState} from 'react'

// const filtre = (tableau, element) => {
//     let value = tableau.filter((valeur) => {
//         return valeur === element
//     })
//     return value
// }

export default function Recherche() {
    const [titre,setTitre] = useState('');
    const [select,setSelect]= useState([])
    const [choose,setChoose] = useState(false)
    let movies = [
        {
            'title': 'Je suis là',
            'auteur': 'Moise MBIYE',
            'year': 2014
        },
        {
            'title': 'Je suis là encore',
            'auteur': 'Moise MBIYE',
            'year': 1996
        },
        {
            'title': 'Royal',
            'auteur': 'Moise MBIYE',
            'year': 2016
        },
        {
            'title': 'Royal encore',
            'auteur': 'Moise Mbiye',
            'year': 2005
        },
        {
            'title': 'Royal et je suis là',
            'auteur': 'Moise MBIYE',
            'year': 1995
        },
        {
            'title': 'Bad Je suis là et royal',
            'auteur': 'Moise MBIYE',
            'year': 1995
        }
    ];
    const music = new Audio(require('./suis.mp3'));

    const filtreVideo = (element)=>{
        // let tab = [];
        if(element.title===titre){
            return element
        }
    }
    return (
        <div className='principale'>
            <input placeholder='Entrez le titre'
            onChange={(e)=>{setTitre(e.target.value)}}
            ></input>
            <button
            onClick={()=>{setSelect(movies.filter(filtreVideo));setChoose(true);}}
            >Chercher</button>
            {
                choose && <span onClick={()=>console.log('On veut jouer cette chanson')}>{select[0].title}</span>
            }
            <div>
                <button onClick={()=>{music.play();}}>Play</button>
                <button onClick={()=>{music.pause();}}>Pause</button>
            </div>
        </div>
    )
}
