import React,{useState,useRef} from 'react'

// const filtre = (tableau, element) => {
//     let value = tableau.filter((valeur) => {
//         return valeur === element
//     })
//     return value
// }

export default function Recherche() {
    const [titre,setTitre] = useState('');
    const [select,setSelect]= useState([])
    const [choose,setChoose] = useState(false)
    let movies = [
        {
            'title': 'Je suis là',
            'auteur': 'Moise MBIYE',
            'year': 2014,
            'song':require('./suis.mp3')
        },
        {
            'title': 'Je suis là encore',
            'auteur': 'Moise MBIYE',
            'year': 1996,
            'song':require('./suis.mp3')
        },
        {
            'title': 'Royal',
            'auteur': 'Moise MBIYE',
            'year': 2016,
            'song':require('./suis.mp3')
        },
        {
            'title': 'Royal encore',
            'auteur': 'Moise Mbiye',
            'year': 2005,
            'song':require('./suis.mp3')
        },
        {
            'title': 'Royal et je suis là',
            'auteur': 'Moise MBIYE',
            'year': 1995,
            'song':require('./suis.mp3')
        },
        {
            'title': 'Je suis là et royal',
            'auteur': 'Moise MBIYE',
            'year': 1995,
            'song':require('./suis.mp3')
        }
    ];

    let music;

    const filtreVideo = (element)=>{
        if(element.title===titre){
            return element
        }
    }
    let ref1 = useRef(null)
    let ref2 = useRef(null)
    return (
        <div className='principale'>
            <input placeholder='Entrez le titre'
            onChange={(e)=>{setTitre(e.target.value)}}
            ></input>
            <button
            onClick={()=>{setSelect(movies.filter(filtreVideo));music.pause();setChoose(true);}}
            >Chercher</button>
            {
                choose && <span onClick={()=>{music = new Audio(select[0].song);music.play();console.log('On est en train de lire cette chanson') }}>{select[0].title}</span>
            }
            <div>
                <button ref={ref1} onClick={()=>{music.play();}}>Play</button>
                <button ref={ref2} onClick={()=>{music.pause();}}>Pause</button>
            </div>
        </div>
    )
}
